package ru.javaprojects.leetcode_tasks.find_the_difference;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class SolutionTest {

    @Test
    void findTheDifference() {
        Solution solution = new Solution();
        assertEquals('e', solution.findTheDifference("abcd", "abcde"));
        assertEquals('y', solution.findTheDifference("", "y"));
        assertEquals('a', solution.findTheDifference("a", "aa"));
        assertEquals('t', solution.findTheDifference("",
                ""));
    }
}